import React from "react";
import "./Modal.css";
import Backdrop from "../../components/UI/Backdrop/Backdrops";
const Modal = props => {
  return (
    <div
      className="Modal"
      style={{
        transform: props.show ? "translateY(0)" : "transalteY(-100vh)",
        opacity: props.show ? "1" : "0"
      }}
    >
      {props.children}
    </div>
  );
};

export default Modal;
